import { createStore } from 'vuex'

export default createStore({
  state: {
    cartList: {
    }
  },
  getters: {
  },
  mutations: {
    addItemToCart (state, payload) {
      const { shopId, productId, productInfo } = payload
      let shopinfo = state.cartList[shopId]
      if (!shopinfo) {
        shopinfo = {}
      }
      let product = shopinfo[productId]
      if (!product) {
        product = productId
      }
    }
  },
  actions: {
  },
  modules: {
  }
})
